import React from "react"
import styles from "./Topdeals.module.css"

import {ShowcaseGridItem} from "./ShowcaseGridItem"

import image1 from "./Extras/topdealsimage1.webp"
import image2 from "./Extras/image2.webp"
import image3 from "./Extras/image3.webp"
import image4 from "./Extras/image4.webp"
import leftbtn from "./Extras/leftbtn.png"
import rightbtn from "./Extras/rightbtn.png"
import showcaseimage1 from "./Extras/showcaseimage1.webp"
import showcaseimage2 from "./Extras/showcaseimage2.webp"
import showcaseimage2_2 from "./Extras/showcaseimage2_2.webp"
import showcaseimage2_3 from "./Extras/showcaseimage2_3.webp"
import showcaseimage3 from "./Extras/showcaseimage3.webp"
import showcaseimage4 from "./Extras/showcaseimage4.webp"
import showcaseimage5 from "./Extras/showcaseimage5.webp"
import showcaseimage6 from "./Extras/showcaseimage6.webp"
import showcaseimage7_1 from "./Extras/showcaseimage7_1.webp"
import showcaseimage7_2 from "./Extras/showcaseimage7_2.webp"
import showcaseimage7_3 from "./Extras/showcaseimage7_3.webp"
import showcaseimage7_4 from "./Extras/showcaseimage7_4.webp"
import showcaseimage8 from "./Extras/showcaseimage8.webp"


export class Topdeals extends React.Component{
  constructor(){
    super()

    this.state={
      index:0,
      imageslider:[image1, image2, image3, image4],
      sliderheaders:["Unleash your School Spirits",
      "Galaxy Tab Series",
      "Galaxy Book series",
      "Odyssey OLED G8"],
      sliderdescription:["Avail benifits upto Rs. 10000",
      "Avail benifits upto Rs. 22000",
      "Avail benifits upto Rs. 17000",
      "Avail benifits upto Rs. 13000"],
      showcaseindex:0,
      ssElements:[[{images:[showcaseimage1], header:'Galaxy Book3, 39.62cm (15.6"), i5, 8GB, Intel Xe Graphics', colors:["light black"], price:74990}, 
      {images: [showcaseimage2, showcaseimage2_2, showcaseimage2_3], header:'Galaxy Tab S8 (Wi-Fi)', colors:["pink", "black", "grey"], price:58990},
      {images: [showcaseimage3], header:'86.8cm (34") G8 OLED Gaming Monitor with 0.1ms response', colors:['grey'], price:109399},
      {images: [showcaseimage4], header:'80.0cm (32") Gaming Monitor with WQHD resolution, 240Hz refresh rate', price:35135}, 
      {images: [showcaseimage5, showcaseimage5, showcaseimage5], header: 'Galaxy Tab S6 Lite (Wi-Fi, 4GB)', colors:["grey","blue", "pink"], price:31999}, 
      {images: [showcaseimage6], header:"Galaxy Tab S8 Ultra (Wi-Fi)", colors:["grey"], price:99999},
      {images: [showcaseimage7_1, showcaseimage7_2, showcaseimage7_3, showcaseimage7_4], colors:['black', 'green', 'light black', 'pink'], price:34999}, 
      {images: [showcaseimage8], header:'Galaxy Book3 Pro, 35.56cm (14"), i7, 16GB, Intel Xe Graphics', colors:['grey'], price:143990}]]
    }
  }

  nextImgSlider(){
    if (this.state.index < this.state.imageslider.length - 1){
      return this.state.index + 1
    }
    else{ return (this.state.imageslider.length - 1) }
  }

  prevImgSlider(){
    if (this.state.index > 0){
      return this.state.index - 1
    }
    else{ return 0 }
  }

  changeColor(index, color){
    return this.state.colors[index][color]
  }

  insertShowcaseElements(){

    return this.state.ssElements[this.state.showcaseindex].map(element => 
      <ShowcaseGridItem images={element.images} colors={element.colors} header={element.header} price={element.price}/>)

    // <ShowcaseGridItem images={[showcaseimage1]} colors={["light black"]} header='Galaxy Book3, 39.62cm (15.6"), i5, 8GB, Intel Xe Graphics' price="74990"/>

    //   <ShowcaseGridItem images={[showcaseimage2, showcaseimage2_2, showcaseimage2_3]} colors={["pink", "black", "grey"]} header='Galaxy Tab S8 (Wi-Fi)' price="58990"/>

    //   <ShowcaseGridItem images={[showcaseimage3]} colors={["grey"]} header='86.8cm (34") G8 OLED Gaming Monitor with 0.1ms response' price="109399"/>

    //   <ShowcaseGridItem images={[showcaseimage4]} colors={["grey"]} header='80.0cm (32") Gaming Monitor with WQHD resolution' price="35135"/>

    //   <ShowcaseGridItem images={[showcaseimage5, showcaseimage5, showcaseimage5]} colors={["grey",
    //   "blue", "pink"]} header='Galaxy Tab S6 Lite (Wi-Fi, 4GB)' price="31999" />

    //   <ShowcaseGridItem images={[showcaseimage6]} colors={["grey"]} header='Galaxy Tab S8 Ultra (Wi-Fi)' price="99999" />

    //   <ShowcaseGridItem images={[showcaseimage7_1, showcaseimage7_2,
    //   showcaseimage7_3, showcaseimage7_4]} colors={["black", "green", "light black", "pink"]} header='Galaxy Tab S7 FE (Wi-Fi)' price="34999" />

    //   <ShowcaseGridItem images={[showcaseimage8]} colors={["grey"]} header='Galaxy Book3 Pro, 35.56cm (14"), i7, 16GB, Intel Xe Graphics' price="143990" />
  }

  render(){

    return(
      <React.Fragment>
      <div className={styles.imageSlider} style={{backgroundImage:`url(${this.state.imageslider[this.state.index]})`}}>
      <button className={styles.leftbtn} onClick={()=>this.setState({index:this.prevImgSlider()})}>
      <img src={leftbtn}/>
      </button>
      <button className={styles.rightbtn} onClick={()=>this.setState({index:this.nextImgSlider()})}>
      <img src={rightbtn}/>
      </button>
      <h1> {this.state.sliderheaders[this.state.index]} </h1>
      <p> {this.state.sliderdescription[this.state.index]} </p>
      <button className={styles.buynow}> Buy Now </button>
      </div>

      <h1 className={styles.headers}> Best Deals </h1>

      <div className={styles.showcase}>
      <ul>
      <li> <button onClick={() => this.setState({showcaseindex: 0})}> Back to School         </button> </li>
      <li> <button> Featured Deals         </button> </li>
      <li> <button> Mobile & Computing      </button> </li>
      <li> <button> Television              </button> </li>
      <li> <button> Wearables & Accessories</button> </li>
      <li> <button> Home Appliances         </button> </li>
      </ul>

      <div className={styles.showcasegrid}>

      {this.insertShowcaseElements()}


      </div>
      </div>

      <h1 className={styles.headers}> Ultimate school bundles </h1>

      </React.Fragment>
      )
  }

}
