import React from "react"
import styles from "./BuyNow.module.css"

export class BuynowBtnComponent extends React.Component{

  constructor(props){
    super(props)

    this.state ={
      isClicked:false,
      header:props.header,
      desc: props.desc,
      backgroundColor: "black",
      textColor: "white"
    }
  }

  Clicked = () => {
    this.setState({
      isClicked: !this.state.isClicked
    }, console.log(this.state.isClicked))

    if (this.state.isClicked==true){
      this.setState({backgroundColor: "white", textColor: "black"})
    }
    else {
      this.setState({backgroundColor: "black", textColor: "white"})
    }
  }

  render(){
    return(
      <React.Fragment>
      <button className={styles.gridItem} style={{backgroundColor:this.state.backgroundColor, textColor: this.state.textColor}} onClick={this.state.Clicked}> 
      <p> this.state.header </p>
      <p> this.state.desc </p>
      </button>
      </React.Fragment>
      )
  }

}
