import React from "react"
import styles from "./Topdeals.module.css"

import image1 from "./Extras/topdealsimage1.webp"
import image2 from "./Extras/image2.webp"
import image3 from "./Extras/image3.webp"
import image4 from "./Extras/image4.webp"
import leftbtn from "./Extras/leftbtn.png"
import rightbtn from "./Extras/rightbtn.png"

export class Topdeals extends React.Component{
  constructor(){
    super()

    this.state={
      index:0,
      imageslider:[image1, image2, image3, image4],
      sliderheaders:["Unleash your School Spirits",
      "Galaxy Tab Series",
      "Galaxy Book series",
      "Odyssey OLED G8"],
      sliderdescription:["Avail benifits upto Rs. 10000",
      "Avail benifits upto Rs. 22000",
      "Avail benifits upto Rs. 17000",
      "Avail benifits upto Rs. 13000"]
    }
  }

  nextImgSlider(){
    if (this.state.index < this.state.imageslider.length - 1){
      return this.state.index + 1
    }
    else{ return (this.state.imageslider.length - 1) }
  }

  prevImgSlider(){
    if (this.state.index > 0){
      return this.state.index - 1
    }
    else{ return 0 }
  }

  render(){

    return(
      <React.Fragment>
      <div className={styles.imageSlider} style={{backgroundImage:`url(${this.state.imageslider[this.state.index]})`}}>
      <button className={styles.leftbtn} onClick={()=>this.setState({index:this.prevImgSlider()})}>
      <img src={leftbtn}/>
      </button>
      <button className={styles.rightbtn} onClick={()=>this.setState({index:this.nextImgSlider()})}>
      <img src={rightbtn}/>
      </button>
      <h1> {this.state.sliderheaders[this.state.index]} </h1>
      <p> {this.state.sliderdescription[this.state.index]} </p>
      <button className={styles.buynow}> Buy Now </button>
      </div>

      <h1 className={styles.headers}> Best Deals </h1>

      <div className={styles.showcase}>
      <ul>
      <li> <button> Back to School         </button> </li>
      <li> <button> Featured Deals         </button> </li>
      <li> <button> Mobile & Computing      </button> </li>
      <li> <button> Television              </button> </li>
      <li> <button> Wearables & Accessories</button> </li>
      <li> <button> Home Appliances         </button> </li>
      </ul>
      </div>
      </React.Fragment>
      )
  }

}
