import React from "react"
import styles from "./Checkout.module.css"

export class Checkout extends React.Component{
  constructor(props){
    super(props)
    this.locationHook=props.myHookValue
    this.exchange = new URLSearchParams(this.locationHook.search).get('exchange');
    this.offername = new URLSearchParams(this.locationHook.search).get('offer').split(",");
    this.samsungCare = new URLSearchParams(this.locationHook.search).get('careplus');
    this.colors = new URLSearchParams(this.locationHook.search).get('color');
    this.device = new URLSearchParams(this.locationHook.search).get('name')
    this.storage = new URLSearchParams(this.locationHook.search).get('storage');
    this.price = new URLSearchParams(this.locationHook.search).get('price');
  }

  isExchange(){
    if(this.exchange != "-1" || this.exchange != "1"){
      return(
        <div className={styles.config}>
        <h1> EXCHANGE </h1>
        <li> Upto </li>
        <li className={styles.price}> Rs. 10000 </li>
        </div>
        )
    }
  }

  isExchange(){
    if(this.exchange != "-1" || this.exchange != "1"){
      return(
        <div className={styles.config}>
        <h1> EXCHANGE </h1>
        <li> Upto </li>
        <li className={styles.price}> Rs. 10000 </li>
        </div>
        )
    }
  }

  isOffer(){
    if(this.offer != "-1"){
      return(
        <div className={styles.config}>
        <h1> OFFER </h1>
        <li> {this.offer.name} </li>
        <li className={styles.price}> Rs. {this.offer.price} </li>
        </div>
        )
    }
  }

  devicedetails(){
    if (this.storage!= "undefined}"){
      return(
        <div className={styles.config}>
        <h1> Device </h1>
        <li> {this.device} </li>
        <li> {this.storage} </li>
        <li className={styles.price}> Rs. {this.price} </li>
        </div>
        )
    }
    else{
      return(
        <div className={styles.config}>
        <h1> Device </h1>
        <li> {this.device} </li>
        <li className={styles.price}> {this.price} </li>
        </div>
        )
    }
  }

  render(){
    return(
    <React.Fragment>
    <div className={styles.container}>

    <img className={styles.deviceImg} src={new URLSearchParams(this.locationHook.search).get('image')}/>
    <div className={styles.deviceConfig}>
    <ul>
    {this.devicedetails()}
    {this.isExchange()}
    {this.isOffer()}
    </ul>
    </div>

    </div>
    </React.Fragment>
    )

  }
}
