import {NavigationBar} from "./Components/NavigationBar"
import {Home} from "./Components/Home"
import {HomeAppliances} from "./Components/HomeAppliances"
import {Mobile} from "./Components/Mobile"
import {Laptops} from "./Components/Laptops"
import {Shop} from "./Components/Shop"
import {TVAAV} from "./Components/TVAAV"
import {Displays} from "./Components/Displays"
import {BuyNow} from "./Components/ShopComponents/BuyNow"
import styles from "./App.css"

import {
  BrowserRouter as Router, Link,
  useLocation
} from "react-router-dom";

export const withMyHook = () => {
  return function WrappedComponent(props) {
    const myHookValue = useLocation();
    return <Component myHookValue={myHookValue} />;
  }
}


function App() {

let component

switch(window.location.pathname){
case "/Samsung":
component = <Home />
break

case "/Samsung/Shop":
component = <Shop />
break

case "/Samsung/HomeAppliances":
component = <HomeAppliances />
break

case "/Samsung/Laptops":
component = <Laptops />
break

case "/Samsung/TVAAV":
component = <TVAAV />
break

case "/Samsung/Mobile":
component = <Mobile />
break

case "/Samsung/BuyNow":
  component = withMyHook(BuyNow)
  break

case "/Samsung/Displays":
component = <Displays />
break
}

  return (
    <div className="App">
      <NavigationBar />
      {component}
    </div>
  );
}

export default App;
